  width: 24px;
  height: 24px;
}

.container.compact-mode .btn span {
  font-size: 13px;
  font-weight: 600;
}

/* 简化模式：调整按钮间距 */
.container.compact-mode .btn:not\(:last-child\) {
  margin-bottom: 0;
}
